import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import { ThemeProvider, useTheme } from './context/ThemeContext';
import { ProgressProvider } from './context/ProgressContext';
import WordPractice from './components/WordPractice';
import SentencePractice from './components/SentencePractice';
import Kanji from './components/Kanji';
import Progress from './components/Progress';
import ThemeToggle from './components/ThemeToggle';

const Navigation: React.FC = () => {
  const location = useLocation();
  const { theme, isDarkMode } = useTheme();

  const getThemeClasses = () => {
    if (isDarkMode) {
      return {
        container: 'bg-dark-card',
        text: 'text-dark-text',
        button: {
          active: 'bg-primary text-white',
          inactive: 'bg-dark-hover text-dark-text hover:bg-dark-border',
        },
      };
    }

    switch (theme) {
      case 'blue':
        return {
          container: 'bg-blue-card',
          text: 'text-blue-text',
          button: {
            active: 'bg-primary text-white',
            inactive: 'bg-blue-hover text-blue-text hover:bg-blue-border',
          },
        };
      case 'green':
        return {
          container: 'bg-green-card',
          text: 'text-green-text',
          button: {
            active: 'bg-primary text-white',
            inactive: 'bg-green-hover text-green-text hover:bg-green-border',
          },
        };
      default:
        return {
          container: 'bg-white',
          text: 'text-gray-800',
          button: {
            active: 'bg-primary text-white',
            inactive: 'bg-gray-50 text-gray-800 hover:bg-gray-100',
          },
        };
    }
  };

  const themeClasses = getThemeClasses();

  const isActive = (path: string) => location.pathname === path;

  return (
    <nav className={`${themeClasses.container} p-4 shadow-md`}>
      <div className="max-w-4xl mx-auto flex justify-between items-center">
        <div className="flex space-x-4">
          <Link
            to="/"
            className={`px-4 py-2 rounded-lg transition-colors ${
              isActive('/') ? themeClasses.button.active : themeClasses.button.inactive
            }`}
          >
            Word Practice
          </Link>
          <Link
            to="/sentence"
            className={`px-4 py-2 rounded-lg transition-colors ${
              isActive('/sentence') ? themeClasses.button.active : themeClasses.button.inactive
            }`}
          >
            Sentence Practice
          </Link>
          <Link
            to="/kanji"
            className={`px-4 py-2 rounded-lg transition-colors ${
              isActive('/kanji') ? themeClasses.button.active : themeClasses.button.inactive
            }`}
          >
            Kanji
          </Link>
          <Link
            to="/progress"
            className={`px-4 py-2 rounded-lg transition-colors ${
              isActive('/progress') ? themeClasses.button.active : themeClasses.button.inactive
            }`}
          >
            Progress
          </Link>
        </div>
        <ThemeToggle />
      </div>
    </nav>
  );
};

const App: React.FC = () => {
  return (
    <Router>
      <ThemeProvider>
        <ProgressProvider>
          <div className="min-h-screen">
            <Navigation />
            <main className="container mx-auto px-4 py-8">
              <Routes>
                <Route path="/" element={<WordPractice />} />
                <Route path="/sentence" element={<SentencePractice />} />
                <Route path="/kanji" element={<Kanji />} />
                <Route path="/progress" element={<Progress />} />
              </Routes>
            </main>
          </div>
        </ProgressProvider>
      </ThemeProvider>
    </Router>
  );
};

export default App;
